export type CCIconOptions = {
    name: string;
    variant: IconVariant;
};
export type Icon = {
    name: string;
    category?: string[];
    tags?: string[];
};
export type CCIconSize = 12 | 16 | 20 | 24 | 32 | 40 | 48 | 64;
export type IconVariant = 'color' | 'glyph';
export type IconParams = Icon & {
    selected?: string;
    size?: CCIconSize;
};
